// モジュールロード
var https = require('https');
var fs = require('fs');

function get(){
  // 出力ファイル名を指定
  var outFile = fs.createWriteStream('fazacoin.zip');

  var url = "https://github.com/endotakashi1992/fazacoin/archive/master.zip"

  // ダウンロード開始
  var req = https.get(url, function (res) {

      // ダウンロードした内容をそのまま、ファイル書き出し。
      res.pipe(outFile);

      // 終わったらファイルストリームをクローズ。
      res.on('end', function () {
          // outFile.close();
      }); 
  });

  // エラーがあれば扱う。
  req.on('error', function (err) {
      console.log('Error: ', err); return;
  });
}

module.exports = get